import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ChannelView = () => {
  const { guildId, channelId } = useParams();
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get(`/api/channels/${guildId}/${channelId}`);
        setMessages(response.data);
      } catch (error) {
        console.error('Error fetching messages:', error);
      }
    };

    fetchMessages();
  }, [guildId, channelId]);

  return (
    <div className="flex h-screen">
      {/* Message view */}
      <div className="flex-1 bg-gray-800 text-white p-4">
        <h1 className="text-2xl mb-4">Channel: {channelId}</h1>
        <div className="space-y-4">
          {messages.map(msg => (
            <div key={msg.message_id} className="bg-gray-700 p-3 rounded-lg shadow">
              <div className="text-sm text-gray-400">
                {msg.user_name} - {new Date(msg.timestamp).toLocaleString()}
              </div>
              <div className="text-lg">{msg.content}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ChannelView;
