import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // To handle navigation
import { Box, Drawer, List, ListItem, ListItemText, Typography, Divider, AppBar, Toolbar, InputBase, IconButton } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import InfiniteScroll from 'react-infinite-scroll-component';
import axios from 'axios';

const drawerWidth = 240;

const ChannelView = () => {
  const { guildId, channelId } = useParams(); // Get the guild and channel params from URL
  const navigate = useNavigate(); // For navigating between guilds/channels

  const [guilds, setGuilds] = useState([]);
  const [selectedGuild, setSelectedGuild] = useState(null);
  const [channels, setChannels] = useState([]);
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);
  const [hasMore, setHasMore] = useState(true);
  const [page, setPage] = useState(1); // For paginating messages
  const [searchTerm, setSearchTerm] = useState('');

  // Fetch guilds when the component mounts
  useEffect(() => {
    const fetchGuilds = async () => {
      try {
        const response = await axios.get('/api/guilds');
        setGuilds(response.data);
        // If guildId is provided in the URL, set the selected guild
        if (guildId) {
          const selected = response.data.find(g => g.db_file === guildId);
          setSelectedGuild(selected);
        }
      } catch (error) {
        console.error('Error fetching guilds:', error);
      }
    };
    fetchGuilds();
  }, [guildId]);

  // Fetch channels when a guild is selected
  useEffect(() => {
    if (selectedGuild) {
      const fetchChannels = async () => {
        try {
          const response = await axios.get(`/api/guilds/${selectedGuild.db_file}/channels`);
          setChannels(response.data);
        } catch (error) {
          console.error('Error fetching channels:', error);
        }
      };
      fetchChannels();
    }
  }, [selectedGuild]);

  // Fetch messages when a channel is selected
  useEffect(() => {
    if (channelId) {
      const fetchMessages = async () => {
        try {
          const response = await axios.get(`/api/guilds/${guildId}/channels/${channelId}?page=${page}`);
          setMessages(response.data);
        } catch (error) {
          console.error('Error fetching messages:', error);
        }
      };
      fetchMessages();
    }
  }, [channelId, page]);

  // Fetch users for the user sidebar
  useEffect(() => {
    if (selectedGuild) {
      const fetchUsers = async () => {
        try {
          const response = await axios.get(`/api/guilds/${selectedGuild.db_file}/users`);
          setUsers(response.data);
        } catch (error) {
          console.error('Error fetching users:', error);
        }
      };
      fetchUsers();
    }
  }, [selectedGuild]);

  // Handle infinite scroll
  const loadMoreMessages = () => {
    setPage(page + 1); // Load next page of messages
  };


  // Handle user selection
  const handleUserSelect = (userId) => {
    navigate(`/guild/${selectedGuild.db_file}/users/${userId}`);
  };



  // Handle channel selection
  const handleChannelSelect = (channelName) => {
    navigate(`/guild/${selectedGuild.db_file}/channel/${channelName}`);
  };

  return (
    <Box sx={{ display: 'flex' }}>
      {/* Left sidebar for channels */}
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
          },
        }}
        variant="permanent"
        anchor="left"
      >
        <Toolbar />
        <Divider />
        <List>
          {guilds.map((guild) => (
            <ListItem button key={guild.db_file} onClick={() => setSelectedGuild(guild)}>
              <ListItemText primary={guild.name} />
            </ListItem>
          ))}
          {channels.map((channel) => (
            <ListItem button key={channel.name} onClick={() => handleChannelSelect(channel.name)}>
              <ListItemText primary={channel.name} />
            </ListItem>
          ))}
        </List>
      </Drawer>

      {/* Main content area for messages */}
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Toolbar />
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1 }}>
              Messages
            </Typography>
            {/* Search Bar */}
            <div style={{ position: 'relative' }}>
              <IconButton>
                <SearchIcon />
              </IconButton>
              <InputBase
                placeholder="Search Messagesâ€¦"
                inputProps={{ 'aria-label': 'search' }}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </Toolbar>
        </AppBar>

        <InfiniteScroll
          dataLength={messages.length}
          next={loadMoreMessages}
          hasMore={hasMore}
          loader={<h4>Loading...</h4>}
        >
          {messages.map((message, index) => (
            <Box key={index} sx={{ my: 2, p: 2, borderRadius: 1, bgcolor: 'grey.800', color: 'white' }}>
              <Typography variant="subtitle2">{message.user_name} - {new Date(message.timestamp).toLocaleString()}</Typography>
              <Typography variant="body1">{message.content}</Typography>
            </Box>
          ))}
        </InfiniteScroll>
      </Box>

      {/* Right sidebar for users */}
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
          },
        }}
        variant="permanent"
        anchor="right"
      >
        <Toolbar />
        <Divider />
        <List>
        {users.map((user) => (
          <ListItem button key={user.user_id} onClick={() => handleUserSelect(user.user_id)}>
            <ListItemText primary={`${user.user_name} (${user.total_messages})`} />
          </ListItem>
        ))}
      </List>
      </Drawer>
    </Box>
  );
};

export default ChannelView;
