import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Box, Drawer, List, ListItem, ListItemText, Typography, Divider, AppBar, Toolbar, InputBase, IconButton } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import InfiniteScroll from 'react-infinite-scroll-component';
import axios from 'axios';

const drawerWidth = 240;

const ChannelView = () => {
  const { guildId, channelId } = useParams(); // Get the guild and channel from the URL
  const navigate = useNavigate(); // To navigate between channels
  const [guilds, setGuilds] = useState([]);
  const [selectedGuild, setSelectedGuild] = useState(null);
  const [channels, setChannels] = useState([]);
  const [messages, setMessages] = useState([]); // Messages state
  const [filteredMessages, setFilteredMessages] = useState([]); // Filtered messages based on search
  const [users, setUsers] = useState([]); // Users state for right sidebar
  const [hasMore, setHasMore] = useState(true); // For infinite scrolling
  const [page, setPage] = useState(1); // Pagination for messages
  const [searchTerm, setSearchTerm] = useState(''); // Search term

  // Fetch guilds when component mounts
  useEffect(() => {
    const fetchGuilds = async () => {
      try {
        const response = await axios.get('/api/guilds');
        setGuilds(response.data);
        if (guildId) {
          const selected = response.data.find(g => g.db_file === guildId);
          setSelectedGuild(selected);
        }
      } catch (error) {
        console.error('Error fetching guilds:', error);
      }
    };
    fetchGuilds();
  }, [guildId]);

  // Fetch channels when a guild is selected
  useEffect(() => {
    if (selectedGuild) {
      const fetchChannels = async () => {
        try {
          const response = await axios.get(`/api/guilds/${selectedGuild.db_file}/channels`);
          setChannels(response.data);
        } catch (error) {
          console.error('Error fetching channels:', error);
        }
      };
      fetchChannels();
    }
  }, [selectedGuild]);

  // Fetch users for the user sidebar
  const fetchUsers = async () => {
    if (selectedGuild) {
      try {
        console.log(`Fetching users for guild: ${selectedGuild.db_file}`);
        const response = await axios.get(`/api/guilds/${selectedGuild.db_file}/users`);
        console.log('Users fetched:', response.data);
        setUsers(response.data);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    }
  };

  // Fetch users when a guild is selected
  useEffect(() => {
    fetchUsers();
  }, [selectedGuild]);

  // Fetch messages for the selected channel with pagination
  useEffect(() => {
    if (channelId) {
      const fetchMessages = async () => {
        try {
          console.log(`Fetching messages for channel: ${channelId}, page: ${page}`);
          const response = await axios.get(`/api/guilds/${guildId}/channels/${channelId}?page=${page}`);
          console.log('Messages fetched:', response.data);

          if (page === 1) {
            setMessages(response.data); // Replace messages on first load
          } else {
            setMessages((prevMessages) => [...prevMessages, ...response.data]); // Append messages for subsequent pages
          }

          if (response.data.length === 0) {
            setHasMore(false); // No more messages to load
          }
        } catch (error) {
          console.error('Error fetching messages:', error);
        }
      };
      fetchMessages();
    }
  }, [channelId, page]);

  // Infinite scroll function
  const loadMoreMessages = () => {
    setPage((prevPage) => prevPage + 1); // Increment the page number
  };

  // Handle search input to filter messages
  useEffect(() => {
    if (searchTerm) {
      const lowercasedFilter = searchTerm.toLowerCase();
      const filteredData = messages.filter((message) =>
        message.content.toLowerCase().includes(lowercasedFilter) ||
        message.user_name.toLowerCase().includes(lowercasedFilter)
      );
      setFilteredMessages(filteredData);
    } else {
      setFilteredMessages(messages); // Reset to all messages if no search term
    }
  }, [searchTerm, messages]);

  // Handle channel selection
  const handleChannelSelect = (channelName) => {
    navigate(`/guild/${selectedGuild.db_file}/channel/${channelName}`);
    setPage(1); // Reset the pagination when switching channels
    setHasMore(true); // Reset infinite scroll state when switching channels
  };

  return (
    <Box sx={{ display: 'flex' }}>
      {/* Left sidebar for channels */}
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
          },
        }}
        variant="permanent"
        anchor="left"
      >
        <Toolbar />
        <Divider />
        <List>
          {guilds.map((guild) => (
            <ListItem button key={guild.db_file} onClick={() => setSelectedGuild(guild)}>
              <ListItemText primary={guild.name} />
            </ListItem>
          ))}
          {channels.map((channel) => (
            <ListItem button key={channel.name} onClick={() => handleChannelSelect(channel.name)}>
              <ListItemText primary={channel.name} />
            </ListItem>
          ))}
        </List>
      </Drawer>

      {/* Main content area for messages */}
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Toolbar />
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1 }}>
              Messages
            </Typography>
            {/* Search Bar */}
            <div style={{ position: 'relative' }}>
              <IconButton>
                <SearchIcon />
              </IconButton>
              <InputBase
                placeholder="Search Messagesâ€¦"
                inputProps={{ 'aria-label': 'search' }}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </Toolbar>
        </AppBar>

        <InfiniteScroll
          dataLength={filteredMessages.length}
          next={loadMoreMessages} // Call loadMoreMessages when the user scrolls down
          hasMore={hasMore}
          loader={<h4>Loading...</h4>}
        >
          {filteredMessages.map((message, index) => (
            <Box key={index} sx={{ my: 2, p: 2, borderRadius: 1, bgcolor: 'grey.800', color: 'white' }}>
              <Typography variant="subtitle2">{message.user_name} - {new Date(message.timestamp).toLocaleString()}</Typography>
              <Typography variant="body1">{message.content}</Typography>
            </Box>
          ))}
        </InfiniteScroll>
      </Box>

      {/* Right sidebar for users */}
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
          },
        }}
        variant="permanent"
        anchor="right"
      >
        <Toolbar />
        <Divider />
        <List>
          {users.map((user) => (
            <ListItem button key={user.user_id}>
              <ListItemText primary={`${user.user_name} (${user.total_messages})`} />
            </ListItem>
          ))}
        </List>
      </Drawer>
    </Box>
  );
};

export default ChannelView;
